import { useState } from "react";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [places, setPlaces] = useState([]);
  const onChange = (event) => {
    setPlace(event.target.value);
  };
  const onSubmit = (event) => {
    event.preventDefault();
    console.log(place);
    if (place === "") {
      return;
    }
    console.log(place);
    setPlaces((prev) => [place, ...prev]);
    console.log(places);
  };

  return (
    <div>
      <div>
        <form onSubmit={onSubmit}>
          <input
            type="text"
            value={place}
            onChange={onChange}
            placeholder="장소"
          ></input>
          <button>추가</button>
          <input placeholder="현재위치"></input>
          <button>검색</button>
        </form>
      </div>
      <ul></ul>
    </div>
  );
}

export default App;
